from rubricas.models.competencias import GrupoCompetencia, Competenciadef crear_gruposCompetencias():    print("Creando Grupos de Competencias")    GrupoCompetencia.objects.all().delete()    gc1 = GrupoCompetencia()    gc1.sigla = "CPP"    gc1.nombre_corto = "Conocimientos para la Práctica"    gc1.nombre_largo = "Conoce, investiga, analiza críticamente y aplica los fundamentos de las ciencias básicas y clínicas a la solución de los problemas de la especialidad."    gc1.save()    gc2 = GrupoCompetencia()    gc2.sigla = "HC"    gc2.nombre_corto = "Cuidado y atención del paciente. Habilidades clínicas y quirúrgicas"    gc2.nombre_largo = "Reúne la información necesaria, estudia, diagnostica, desarrolla y aplica planes de prevención, tratamiento y rehabilitación necesarios para el cuidado integral de los pacientes de su especialidad. Analiza críticamente este proceso y su evolución identificando problemas o inconsistencias y puede replantear, corregir y mejorar el cuidado del paciente."    gc2.save()    gc3 = GrupoCompetencia()    gc3.sigla = "ABP"    gc3.nombre_corto = "Aprendizaje basado en la práctica y el mejoramiento"    gc3.nombre_largo = "Reconoce la importancia y las limitaciones de la práctica médica estandarizada y basada en la evidencia científica. Identifica problemas o inconsistencias en su práctica médica, busca y analiza críticamente la literatura para dar solución a los problemas planteados."    gc3.save()def crear_competencia():    Competencia.objects.all().delete()    crear_gruposCompetencias()    print("Creando competencias")    c1 = Competencia()    c1.sigla = "HC1"    c1.nombre = "Reúne la información necesaria de manera organizada, identificando y profundizando en datos clínicos relevantes. valora la salud, la dinámica familiar el contexto social."    c1.grupo = GrupoCompetencia.buscar_por_sigla("HC")    c1.save()    c1 = Competencia()    c1.sigla = "HC2"    c1.nombre = "Realiza un examen organizado, completo y minucioso, identificando los signos clínicos relevante, teniendo en cuenta la situación y necesidades del paciente."    c1.grupo = GrupoCompetencia.buscar_por_sigla("HC")    c1.save()    c1 = Competencia()    c1.sigla = "HC3"    c1.nombre = "Sintetiza, analiza la información y establece diagnósticos diferenciales y planes de estudio y tratamiento apropiados."    c1.grupo = GrupoCompetencia.buscar_por_sigla("HC")    c1.save()